// Pega o item de entrada.
const item = items[0];

// --- IMPORTANTE ---
// Substitua 'seuCampoDeData' abaixo pelo nome real do campo que contém a data no seu item.
// Exemplo: se a data vem de um campo chamado "createdAt", use item.json.createdAt
const dataStringISO = item.json.currentDate; 

// Cria um objeto Date a partir da string. O JavaScript entende o formato ISO 8601 nativamente.
const data = new Date(dataStringISO);

// Extrai os componentes da data
// .padStart(2, '0') garante que sempre teremos dois dígitos (ex: 07 em vez de 7)
const dia = String(data.getDate()).padStart(2, '0');
const mes = String(data.getMonth() + 1).padStart(2, '0'); // getMonth() retorna 0-11, por isso somamos 1
const ano = data.getFullYear();
const horas = String(data.getHours()).padStart(2, '0');
const minutos = String(data.getMinutes()).padStart(2, '0');
const segundos = String(data.getSeconds()).padStart(2, '0');

// Monta as strings nos formatos desejados
const dataFormatada = `${dia}/${mes}/${ano}`;
const dataHoraFormatada = `${horas}:${minutos}`;

// Adiciona os novos campos com as datas formatadas ao item.
// Você pode usar qualquer um dos formatos abaixo no restante do seu workflow.
item.json.data_formatada = dataFormatada;
item.json.data_hora_formatada = dataHoraFormatada;

// Retorna o item modificado para o próximo nó.
return item;

