const nomeCompleto = $('Ticket').first().json.Nome || '';
const primeiroNome = nomeCompleto.trim().split(' ')[0] || '';

return {
  json: {
    primeiroNome,
  }
};

// Obtém a data original em string e converte para objeto Date
const inputDateStr = $('Data').first().json.data;
const inputDate = new Date(inputDateStr);

// Subtrai 1 hora manualmente (ajuste de -04:00 para -03:00)
inputDate.setHours(inputDate.getHours() - 3);

// Formata a data no padrão brasileiro
const dia = String(inputDate.getDate()).padStart(2, '0');
const mes = String(inputDate.getMonth() + 1).padStart(2, '0');
const ano = inputDate.getFullYear();
const hora = String(inputDate.getHours()).padStart(2, '0');
const minuto = String(inputDate.getMinutes()).padStart(2, '0');

const dataFormatada = `${dia}/${mes}/${ano} ${hora}:${minuto}`;

// Retorna resultado
return [
  {
    json: {
      dataFormatada
    }
  }
];
