// Lista de campos que devem ser tratados como data
const DATE_FIELDS = [
  'date_created',
  'date_updated',
  'date_closed',
  'date_done',
  'due_date',
  'start_date'
];

// Função auxiliar para padronizar zero à esquerda
const pad = (n) => n.toString().padStart(2, '0');

// Função para converter timestamp (em milissegundos) para formato DD/MM/YYYY HH:mm
function formatDate(ts) {
  if (ts === null || ts === undefined || ts === '') return null;
  const num = Number(ts);
  if (!Number.isFinite(num)) return null;
  const d = new Date(num);
  if (isNaN(d.getTime())) return null;

  const dia = pad(d.getDate());
  const mes = pad(d.getMonth() + 1);
  const ano = d.getFullYear();
  const hora = pad(d.getHours());
  const min = pad(d.getMinutes());
  return `${dia}/${mes}/${ano} ${hora}:${min}`;
}

// Mapeia todos os items de entrada
const out = items.map(item => {
  const obj = { ...(item.json || {}) };

  // Converte apenas os campos de data definidos
  DATE_FIELDS.forEach(field => {
    if (obj[field] !== undefined) {
      obj[field] = formatDate(obj[field]);
    }
  });

  return { json: obj };
});

return out;

