// Campos que devem ser tratados como data
const DATE_FIELDS = [
  'entrada'
];

// Zero-left
const pad = n => n.toString().padStart(2, '0');

// Tenta converter valores diversos para Date
function toDate(value) {
  if (value === null || value === undefined || value === '') return null;

  // n√∫mero ou string num√©rica em ms
  if (!isNaN(value)) {
    const n = Number(value);
    const d = new Date(n);
    return isNaN(d.getTime()) ? null : d;
  }

  // DD/MM/YYYY HH:mm
  const m = String(value).match(/^(\d{2})\/(\d{2})\/(\d{4})(?:\s+(\d{2}):(\d{2})(?::(\d{2}))?)?$/);
  if (m) {
    const [ , dd, MM, yyyy, HH = '00', mm = '00', ss = '00'] = m;
    const d = new Date(Number(yyyy), Number(MM) - 1, Number(dd), Number(HH), Number(mm), Number(ss));
    return isNaN(d.getTime()) ? null : d;
  }

  // ISO ou formatos reconhec√≠veis pelo JS
  const d = new Date(value);
  return isNaN(d.getTime()) ? null : d;
}

// Formata para YYYY-MM-DD HH:mm:ss
function formatSQL(d) {
  if (!d) return null;
  const yyyy = d.getFullYear();
  const MM = pad(d.getMonth() + 1);
  const dd = pad(d.getDate());
  const HH = pad(d.getHours());
  const mm = pad(d.getMinutes());
  const ss = pad(d.getSeconds());
  return `${yyyy}-${MM}-${dd}`;
}

const out = items.map(item => {
  const obj = { ...(item.json || {}) };

  DATE_FIELDS.forEach(field => {
    if (obj[field] !== undefined) {
      const dt = toDate(obj[field]);
      obj[`${field}_filter`] = formatSQL(dt); // ex: 2025-10-01 00:00:00
    }
  });

  return { json: obj };
});

return out;

